pragma solidity ^0.5.0;
pragma experimental ABIEncoderV2;

import './ECRecovery.sol';

contract SimpleTransfer {
    struct StateObject {
        address owner;
        address predicate;
    }

    struct Witness {
        bytes signature;
    }

    function bytesToStateObject(
        bytes memory _state
    ) internal pure returns (StateObject memory) {
        (address owner, address predicate) = abi.decode(_state, (address, address));
        return StateObject(
            owner,
            predicate
        );
    }

    function bytesToWitness(
        bytes memory _witness
    ) internal pure returns (Witness memory) {
        bytes memory signature = abi.decode(_witness, (bytes));
        return Witness(
            signature
        );
    }

    function validStateTransition(
        bytes memory _oldState,
        bytes memory _newState,
        bytes memory _witness
    ) public pure returns (bool) {
        StateObject memory oldState = bytesToStateObject(_oldState);
        Witness memory witness = bytesToWitness(_witness);

        // Check the transaction signature.
        bytes32 txhash = keccak256(_newState);
        bool validSignature = (ECRecovery.recover(txhash, witness.signature) == oldState.owner);

        return validSignature;
    }
}
